import { makeAutoObservable, runInAction } from 'mobx';

export default class MenuStore {
    isMenuDDLOpen: boolean = false;

    constructor() {
        makeAutoObservable(this);
    }

    toggleMenuDDL = () => {
        this.isMenuDDLOpen = !this.isMenuDDLOpen;
    }
}
